**************控制项目征集的img******************************/        $(".project_search li:first-child").children('a').children('img').css({            marginTop: "4px",            marginLeft: "4px"        });    }    function show_num(t,n){       // t.html(n);return;        var it =t.find("i");        var len = String(n).length;        for(var i=0;i<len;i++){            if(it.length<=i){                t.append("<i></i>");            }            var num=String(n).charAt(i);            var y = -parseInt(num)*25; //y轴位置            var obj = t.find("i").eq(i);            obj.animate({ //滚动动画                        backgroundPosition :'(0 '+String(y)+'px)'                    }, 'slow','swing',function(){}            );        }    }</script>
